/**
 * 事务
 * https://goframe.org/docs/core/gdb-transaction
 */
func (h *Hello) TransactionData(req *ghttp.Request) {
	data := g.Map{
		"id":           11,
		"name":         "Linux驱动开发入门与实践",
		"author":       "郑强",
		"price":        45,
		"publish_time": gdb.Raw("now()"),
	}

	g.DB().Transaction(context.TODO(), func(ctx context.Context, tx gdb.TX) error {
		_, err := tx.Model("book").Ctx(ctx).Save(data)
		return err
	})

	// dao使用
	books, err := dao.Book.Ctx(context.TODO()).WhereGT("id", 3).WhereLT("id", 6).All()
	if err == nil {
		req.Response.WriteJson(books)
	}

	// dao字段过滤-会过滤nil值
	book := do.Book{
		Name:        "Linux驱动开发入门与实践1",
		Price:       0,
		PublishTime: nil,
	}

	dao.Book.Ctx(context.TODO()).Where("id", 11).Data(book).Update()

	// dao字段过滤-通过对象处理，可以设置nil值，但是如果不设置，就会把字段置空
	type Book struct {
		Id      uint
		Name    string
		Author  string
		Price   float64
		PubTime *gtime.Time `orm:"publish_time"`
	}
	bookStruct := Book{
		Id:      10,
		Name:    "Linux驱动开发入门与实践",
		Author:  "反馈",
		Price:   0.32,
		PubTime: nil,
	}
	dao.Book.Ctx(context.TODO()).Where("id", 10).Data(bookStruct).Update()
}

/**
 * 关联查询
 * https://goframe.org/docs/core/gdb-chaining-relation
 */
func (h *Hello) RelationSelect(req *ghttp.Request) {
	// 一对一查询：员工列表，每个员工都有一个爱好和对应一个部门
	type MyDept struct {
		g.Meta `orm:"table:dept"`
		Id     uint   `json:"id"     ` // ID
		Name   string `json:"name"   ` // 部门名称
		Leader string `json:"leader" ` // 部门领导
		Phone  string `json:"phone"  ` // 联系电话
	}
	type Hobby struct {
		g.Meta `orm:"table:hobby"`
		Id     uint   `json:"id"     ` // ID
		EmpId  uint   `json:"emp_id" ` // 员工ID
		Name   string `json:"name"   ` // 爱好名称
	}
	type MyEmp struct {
		g.Meta `orm:"table:emp"`
		Id     uint   `json:"id"      ` // ID
		DeptId uint   `json:"dept_id" ` // 所属部门
		Name   string `json:"name"    ` // 姓名
		Gender int    `json:"gender"  ` // 性别: 0=男 1=女
		Phone  string `json:"phone"   ` // 联系电话
		Email  string `json:"email"   ` // 邮箱
		Avatar string `json:"avatar"  ` // 照片

		Dept  *MyDept `orm:"with:id=dept_id" json:"dept"`
		Hobby *Hobby  `orm:"with:emp_id=id" json:"hobby"`
	}

	var emps []*MyEmp
	err := dao.Emp.Ctx(context.TODO()).WithAll().Scan(&emps)
	if err == nil {
		req.Response.Writeln("员工信息：")
		req.Response.WriteJson(emps)
	}

	// 一对多查询：部门列表，一个部门有多个员工
	type Dept struct {
		Id     uint   `json:"id"     ` // ID
		Pid    uint   `json:"pid"    ` // 上级部门ID
		Name   string `json:"name"   ` // 部门名称
		Leader string `json:"leader" ` // 部门领导
		Phone  string `json:"phone"  ` // 联系电话

		Emps []*MyEmp `orm:"with:dept_id=id" json:"emps"`
	}

	var depts []*Dept
	err = dao.Dept.Ctx(context.TODO()).With(MyEmp{}).Scan(&depts)
	if err == nil {
		req.Response.Writeln("部门信息：")
		req.Response.WriteJson(depts)
	}

}

